def headers = messageExchange.getResponseHeaders();
log.info("Spotify Search - Header content: " + headers)
log.info("Raw Status: " + headers["#status#"])
def respStatus = headers["#status#"].toString().find("[0-9]{3}").toInteger()
log.info("Resp Status: " + respStatus)
assert respStatus ==200, "Invalid response status " +respStatus

